import React from "react";
import classnames from "classnames";
// plugin that creates slider
import Slider from "nouislider";
// react plugin used to create switch buttons
import Switch from "react-bootstrap-switch";
import { Link } from "react-router-dom";
import ImageButton from "components/UIComponents/imageButton3"
import axios from 'axios';

// reactstrap components
import {
  Button,
  Label,
  FormGroup,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";


axios.get('/api/products');

class Basics extends React.Component {

  state={
    products:""
  }

  constructor(props) {
    super(props);
    this.state = {
      inputFocus: false
    };
  }
  componentDidMount(){
    this.callApi()
      //body로 담은 고객 목록을 받아서 
      //이 목록을 state로 설정해주는것
      //결과적으로 body가 res라는 변수이름으로 바뀌고
      //그것을 customers 변수값에 넣어줌
      .then(res => this.setState({products: res}))
      //만약 오류가 발생하는경우 콘솔창에 오류를 보여준다.
      .catch(err => console.log(err));
  }
  
  callApi = async()=>{
    //접속하고자 하는 api주소를 넣어줌
    const response = await fetch('/api/products');
    //출력한 데이터를 json으로 만들어서 body라는 변수에 넣어줌
    const body = await response.json();
    return body;
  }


  render() {
    return (
        <Container>
          
         
          
          <div id="images">

          <Row>
            <Col md="1">
            <h1>
              NEW
            </h1>
            </Col>
            <Col md="9">
              <Button
                className="btn-simple btn-round"
                color="primary"
                type="button"
                Link tag={Link} to="/register-page"
              >
                + More
              </Button>
            </Col>
          </Row>
          
            <Row>

            <Row>
          {/* <ImageButton imagePath="assets/img/gucci.jpg" linkPage="/product-page" itemName="GUCCI Snake wallet" itemPrice="41 ETH"></ImageButton> */}
         
          <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/gucci4.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
            <p>GUCCI</p>
            <h5>41 ETH</h5>
        </Col>

        <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page2';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/ballpen.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
            <p>MONTBLAC ballpen</p>
            <h5>9 ETH</h5>
        </Col>

        <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page2';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/dior.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
            <p>Dior belt</p>
            <h5>17 ETH</h5>
        </Col>

        <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/patek.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
            <p>patekphilippe watch</p>
            <h5>555 ETH</h5>
        </Col>

        </Row>

        
      
            </Row>

          {/* ---------------------------------------- */}
          <div class="space-70"></div>
          <Row>
            <Col md="1">
            <h1>
              OLD
            </h1>
            </Col>
            <Col md="9">
              <Button
                className="btn-simple btn-round"
                color="info"
                type="button"
                Link tag={Link} to="/landing-page"
              >
                + More
              </Button>
            </Col>
          </Row>


            <Row>
            <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/air jordan old.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
            <p>Air Jordan shoes</p>
            <h5>33 ETH</h5>
        </Col>
              
        <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/chanel old.jpg")}
              style={{ width: "250px" }}
             
            />
          </button>
                  <p>chanel bag</p>
                  <h5>37 ETH</h5>

              </Col>

              <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/1111.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
                  <p>SAINT LAURENT pouch</p>
                  <h5>29 ETH</h5>
              </Col>

              <Col className="mt-5 mt-sm-0" sm="3" xs="6">
          <button type="button" onClick={(e) => {
                                        e.preventDefault();
                                        window.location.href='/product-page';
          }}>
            <img
              alt="..."
              className="img-fluid rounded shadow-lg"
              src={require("assets/img/bal.jpg")}
              style={{ width: "250px" }}
              
            />
          </button>
                  <p>balenciaga wallet</p>
                  <h5>49 ETH</h5>
              </Col>
            </Row>

          </div>

          <div class="space-70"></div>


        
        </Container>
      
    );
  }
}

export default Basics;
